
public class EvenandOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int[]number={25,22,1,5,14};

int count=0;
int count1=0;

for(int i=0; i<5; i++){
	if ((number[i]%2) == 0){
		count ++;
	}
	else
	{
count1 ++;

}
}
System.out.println("The number of even numbers are "+ count +"AND"+ "" + "The number of odd numbers are"+ count1);
	}}
	
